import React, { useEffect, useState } from 'react'
import { gql, useQuery } from '@apollo/client'

const GET_BOOKS = gql`
  query allBooks($genre: String){
    allBooks(genre: $genre){
      title
      published
      author{
        name
      }
      genres
    }
  }
`
const Books = (props) => {
  const [filter, setFilter] = useState(null)
  const [getBooks, result] = useQuery(GET_BOOKS)

  const allBooks = getBooks()
  console.log('allBooks: ', allBooks)

  useEffect(() => {
    const books = getBooks({variables: {genre: filter}})
  }, [filter])

  const onlyUnique = (value, index, self) => {
    return self.indexOf(value) === index;
  }
  const genres = allBooks.flatMap((b) => b.genres).filter(onlyUnique)

  if (!props.show) {
      return null
    }

  if (result.loading) {
    return(
      <div>
        loading...
      </div>
    )
  }

  return (
    <div>
      <h2>books</h2>
      {filter ? <div>in genre {filter}</div>: <div>showing all</div>}
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
          </tr>
          {books.map((b) => (
            <tr key={b.title}>
              <td>{b.title}</td>
              <td>{b.author.name}</td>
              <td>{b.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        {genres.map((g) => 
          <button key={g} onClick={() => setFilter(g)}>{g}</button>
        )}
        <button key={'empty'} onClick={() => setFilter(null)}>all genres</button>
      </div>
    </div>
  )
}

export default Books
